def findWord(word: str) -> str:
    result = "Ðš ÑÐ¾Ð¶Ð°Ð»ÐµÐ½Ð¸ÑŽ, Ñ Ð½Ðµ Ð·Ð½Ð°ÑŽ Ñ‚Ð°ÐºÐ¾Ð³Ð¾ ÑÐ»Ð¾Ð²Ð°."
    target = word.lower().strip()

    for i in words:
        if i.lower() == target:
            return words.get(i)
        
    return result

